import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, ScrollView } from 'react-native';
import { initializeApp } from '@firebase/app';
import { getAuth, signInWithEmailAndPassword } from '@firebase/auth';
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import admin from './admin';

const firebaseConfig = {
  apiKey: "AIzaSyCl-an8koZ0QbMfL6PahYlO5SuPOzyQEpg",
  authDomain: "winged-bonito-417718.firebaseapp.com",
  projectId: "winged-bonito-417718",
  storageBucket: "winged-bonito-417718.appspot.com",
  messagingSenderId: "995129372816",
  appId: "1:995129372816:web:d596b2a60ff9a9f90d6815",
  measurementId: "G-LWQPZGG7F3"
};

const app = initializeApp(firebaseConfig);
const Stack = createStackNavigator();

const AuthScreen = ({ email, setEmail, password, setPassword, handleAuthentication }) => {
  return (
    <View style={styles.authContainer}>
       <Text style={styles.title}>Sign In</Text>
       <TextInput
        style={styles.input}
        value={email}
        onChangeText={setEmail}
        placeholder="Email"
        autoCapitalize="none"
      />
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={setPassword}
        placeholder="Password"
        secureTextEntry
      />
      <View style={styles.buttonContainer}>
        <Button title="Sign In" onPress={handleAuthentication} color="#3498db" />
      </View>
    </View>
  );
}

export default function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const auth = getAuth(app);

  const handleAuthentication = async () => {
    try {
      if (email === 'admin' && password === 'admin') {
        console.log('Navigating to AdminScreen...');
        // Navigation logic to navigate to AdminScreen
        navigation.navigate('Admin');
      } else {
        // Regular login functionality
        await signInWithEmailAndPassword(auth, email, password);
        console.log('User signed in successfully!');
      }
    } catch (error) {
      console.error('Authentication error:', error.message);
    }
  };

  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Auth" component={AuthScreen} />
        <Stack.Screen name="admin" component={admin} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  authContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
    backgroundColor: '#e9e9e9',
  },
  title: {
    fontSize: 24,
    marginBottom: 16,
  },
  input: {
    height: 40,
    width: '80%',
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 16,
    paddingHorizontal: 10,
    borderRadius: 8,
  },
  buttonContainer: {
    width: '80%',
    marginBottom: 16,
  },
});
