import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, StyleSheet, ScrollView } from 'react-native';
import { initializeApp } from '@firebase/app';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged, signOut } from '@firebase/auth';


const firebaseConfig = {
  apiKey: "AIzaSyCl-an8koZ0QbMfL6PahYlO5SuPOzyQEpg",
  authDomain: "winged-bonito-417718.firebaseapp.com",
  projectId: "winged-bonito-417718",
  storageBucket: "winged-bonito-417718.appspot.com",
  messagingSenderId: "995129372816",
  appId: "1:995129372816:web:d596b2a60ff9a9f90d6815",
  measurementId: "G-LWQPZGG7F3"
};

const app = initializeApp(firebaseConfig);

const AuthScreen = ({ email, setEmail, password, setPassword, handleAuthentication }) => {
  return (
    <View style={styles.authContainer}>
       <Text style={styles.title}>Sign In</Text>

       <TextInput
        style={styles.input}
        value={email}
        onChangeText={setEmail}
        placeholder="Email"
        autoCapitalize="none"
      />
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={setPassword}
        placeholder="Password"
        secureTextEntry
      />
      <View style={styles.buttonContainer}>
        <Button title="Sign In" onPress={handleAuthentication} color="#3498db" />
      </View>
    </View>
  );
}

const AdminScreen = () => {
  return (
    <View style={styles.authContainer}>
      <Text style={styles.title}>Admin Screen</Text>
      <Text>Welcome, Admin!</Text>
    </View>
  );
}

export default App = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const auth = getAuth(app);

  const handleAuthentication = async () => {
    try {
      if (email === 'admin' && password === 'admin') {
        // If user enters admin credentials, navigate to AdminScreen
        console.log('Navigating to AdminScreen...');
        // Implement navigation logic here
      } else {
        // Regular login/signup functionality
        // Implement login/signup logic here
      }
    } catch (error) {
      console.error('Authentication error:', error.message);
    }
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <AuthScreen
        email={email}
        setEmail={setEmail}
        password={password}
        setPassword={setPassword}
        handleAuthentication={handleAuthentication}
      />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
    backgroundColor: '#f0f0f0',
  },
  authContainer: {
    width: '80%',
    maxWidth: 400,
    backgroundColor: '#fff',
    padding: 16,
    borderRadius: 8,
    elevation: 3,
  },
  title: {
    fontSize: 24,
    marginBottom: 16,
    textAlign: 'center',
  },
  input: {
    height: 40,
    borderColor: '#ddd',
    borderWidth: 1,
    marginBottom: 16,
    padding: 8,
    borderRadius: 4,
  },
  buttonContainer: {
    marginBottom: 16,
  },
});
